CREATE TABLE IF NOT EXISTS CAR (id INT NOT NULL AUTO_INCREMENT, serie INT NOT NULL,marca VARCHAR(50),modelo INT NOT NULL,precio INT,color VARCHAR(50));
CREATE TABLE IF NOT EXISTS SALE (id INT NOT NULL AUTO_INCREMENT, car INT NOT NULL, cc INT NOT NULL, client VARCHAR(150),date VARCHAR(20));
CREATE TABLE IF NOT EXISTS INVENTORY (id INT NOT NULL AUTO_INCREMENT,car INT NOT NULL, inventory INT NOT NULL, sales INT NOT NULL);